<?php/* * 2016.09.02	softsyw * Desc : 관리자 > 공지사항 리스트 * 		App : App 메인 화면의 공지 * 		Web : 모바일웹 메인 화면의 공지 * 		post : 모든 담벼락 * 		chat : 모든 채팅 *  */require_once '../../wps-config.php';require_once INC_PATH . '/classes/WpsPaginator.php';$post_type = 'notice_new';// page number$page = empty($_GET['page']) ? 1 : $_GET['page'];// search$comics_brand = empty($_GET['comics_brand']) ? '' : $_GET['comics_brand'];$sts = empty($_GET['status']) ? '' : $_GET['status'];$qa = empty($_GET['qa']) ? '' : trim($_GET['qa']);$q = empty($_GET['q']) ? '' : trim($_GET['q']);$sql = '';$pph = '';$sparam = [];// Simple searchif ( empty($qa) ) {    if ( !empty($q) ) {        $sql = " AND ( p.post_content LIKE ? OR p.post_title LIKE ? OR p.post_name LIKE ? ) ";        array_push( $sparam, '%'.$q.'%', '%'.$q.'%', '%'.$q.'%' );    }} else {    if ( !empty($q) ) {        if ( !strcmp($qa, 'isbn') ) {            $sql = " AND $qa = ?";            array_push( $sparam, $q );        } else {            $sql = " AND $qa LIKE ?";            array_push( $sparam, '%'.$q.'%' );        }    }}if ($sts) {    $sql .= " AND p.post_type_area LIKE ?";    array_push( $sparam, '%'.$sts.'%' );}if ($comics_brand) {    $sql .= " AND comics_brand = ? ";    array_push( $sparam, $comics_brand );}// Positional placeholder ?if ( !empty($sql) ) {    $pph_count = substr_count($sql, '?');    for ( $i = 0; $i < $pph_count; $i++ ) {        $pph .= 's';    }}if (!empty($pph)) {    array_unshift($sparam, $pph);}$query = "	SELECT		ID,		news_title,		news_sub_title,		news_content,		comics_brand,		read_cnt,		share_cnt,		comments_cnt,		open_yn,		main_view_yn,		img_thumb_url,		create_id,		update_id,		created_dt,		updated_dt	FROM		bt_news	WHERE		1 = 1		$sql	ORDER BY		created_dt DESC";$paginator = new WpsPaginator($wdb, $page);$rows = $paginator->ls_init_pagination( $query, $sparam );$total_count = $paginator->ls_get_total_rows();require_once ADMIN_PATH . '/admin-header.php';require_once './pages-lnb.php';?>    <link rel="stylesheet" href="<?php echo INC_URL ?>/css/jquery-ui.css">    <!-- Content Wrapper. Contains page content -->    <div class="content-wrapper">        <!-- Content Header (Page header) -->        <div class="content-header">            <h1>                <?php //echo $wps_post_type[$post_type] ?> 뉴스관리 &nbsp;            </h1>            <ol class="breadcrumb">                <li><a href="<?php echo ADMIN_URL ?>/admin.php"><i class="fa fa-dashboard"></i> Home</a></li>                <li><a href="<?php echo ADMIN_URL ?>/pages/">페이지 관리</a></li>                <li class="active"><b>뉴스관리</b></li>            </ol><!--            <div class="well bg-info" style="margin-top:2rem;background: #fff;">-->               <p style="margin-top:1rem;">메인 뉴스 영역에 노출되는 뉴스 콘텐츠를 관리합니다.</p><!--            </div>-->        </div>        <!-- Main content -->        <div class="content body">            <div class="box box-default">                <div class="box-body">                    <form id="search-form" class="form-inline">                        <input type="hidden" name="status" value="<?php echo $sts ?>">                        <div class="row">                            <div class="col-sm-6">                                <div class="form-group">                                    <a href="<?php echo $_SERVER['PHP_SELF'] ?>" class="btn <?php echo empty($sts) ? 'btn-success' : 'btn-info'; ?> btn-flat">전체</a>                                    <a href="?comics_brand=1" class="btn btn-info btn-flat">마블</a>                                    <a href="?comics_brand=2" class="btn btn-info btn-flat">디씨</a>                                    <a href="?comics_brand=3" class="btn btn-info btn-flat">이미지</a>                                </div>                                <div class="form-group" style="margin-left:2rem;">                                    <select name="qa" class="form-control">                                        <optgroup label="키워드 검색">                                            <option value="">-전체-</option>                                            <option value="news_title" <?php echo strcmp($qa, 'news_title') ? '' : 'selected'; ?>>제목</option>                                            <option value="news_content" <?php echo strcmp($qa, 'news_content') ? '' : 'selected'; ?>>내용</option>                                        </optgroup>                                    </select>                                </div>                                <div class="form-group">                                    <div class="input-group input-group-sm">                                        <input type="text" name="q" value="<?php echo $q ?>" class="form-control">												<span class="input-group-btn">													<button type="submit" class="btn btn-primary btn-flat">검색</button>												</span>                                    </div>                                </div>                            </div>                            <div class="col-sm-6 text-right">                                <a href="./news_new.php" class="btn btn-default">뉴스 등록</a>                                <button type="button" id="btn-delete" class="btn btn-danger ">선택한 뉴스 삭제</button>                            </div>                            <div class="col-sm-2"></div>                        </div><!-- /.row -->                    </form>                </div><!-- /.box -->            </div>            <form id="news-list-form">                <div class="box box-primary">                    <div class="box-header">                        <i class="fa fa-circle-o text-yellow"></i> Total: <b><?php echo number_format($total_count) ?></b>                    </div>                    <div class="box-body">                        <table class="table table-striped table-hover">                            <thead>                            <tr>                                <th><input type="checkbox" id="switch-all"></th>                                <th>#</th>                                <th>이미지</th>                                <th>뉴스 제목</th>                                <th width="100px;">코믹스 브랜드</th><!--                                <th>미리보기</th>-->                                <th>공개</th>                                <th>메인노출 </th>                                <th>조회</th>                                <th>공유</th>                                <th>댓글</th>                                <th>작성일</th>                            </tr>                            </thead>                            <tbody>                            <?php                            if ( !empty($rows) ) {                                foreach ( $rows as $key => $val ) {                                    $news_id = $val['ID'];                                    $img_thumb_url = $val['img_thumb_url'];                                    $news_title = htmlspecialchars($val['news_title']);                                    $news_sub_title = htmlspecialchars($val['news_sub_title']);                                    $comics_brand = $val['comics_brand'];                                    $open_yn = $val['open_yn'];                                    $main_view_yn = $val['main_view_yn'];                                    $read_cnt = $val['read_cnt'];                                    $share_cnt = $val['share_cnt'];                                    $comments_cnt = $val['comments_cnt'];                                    $created_dt = $val['created_dt'];                                    ?>                                    <tr>                                        <td><input type="checkbox" class="news_list" name="news_list[]" value="<?php echo $news_id ?>"></td>                                        <td>