v class="box-footer text-center">                        <div class="pull-left"><!--                            <button type="button" id="btn-delete" class="btn btn-danger btn-sm">삭제</button>-->                        </div>                        <?php echo $paginator->ls_bootstrap_pagination_link(); ?>                    </div>                </div><!-- /.box -->            </form>        </div><!-- /.content -->    </div><!-- /.content-wrapper -->    <div class="dialog" id="dialog-preview" title="미리보기">        <table class="table table-bordered">            <colgroup>                <col style="width: 20%; min-width: 80px;">                <col>            </colgroup>            <tbody>            <tr>                <td class="active"></td>                <td id="prv-title"></td>            </tr>            <tr>                <td class="active">제목</td>                <td id="prv-title"></td>            </tr>            <tr>                <td class="active">이름</td>                <td id="prv-nickname"></td>            </tr>            <tr>                <td class="active">첨부파일</td>                <td id="prv-attachment"></td>            </tr>            <tr>                <td class="active">본문</td>                <td><div style="width: 690px; overflow-x: auto;" id="prv-content"></div></td>            </tr>            </tbody>        </table>    </div>    <!-- /.dialog -->    <script src="<?php echo INC_URL ?>/js/ls-util.js"></script>    <script src="<?php echo INC_URL ?>/js/jquery/jquery.oLoader.min.js"></script>    <script src="<?php echo INC_URL ?>/js/jquery-ui.min.js"></script>    <script>        $(function() {            $("#switch-all").click(function() {                var chk = $(this).prop("checked");                $(".news_list").prop("checked", chk);            });            $("#dialog-preview").dialog({                autoOpen: false,                width: 900,                height: 550,                buttons: {                    "닫기": function() {                        $(this).dialog("close");                    }                }            });            $('a[class*="preview-notice-"]').click(function(e) {                e.preventDefault();                var id = $(this).attr("class").replace(/\D/g, "");                $.ajax({                    type : "POST",                    url : "./ajax/news-preview.php",                    data : {                        "id" : id                    },                    dataType : "json",                    success : function(res) {                        if (res.code == "0") {                            $("#prv-title").html(res.posts.post_title);                            $("#prv-nickname").html(res.posts.post_name);                            $("#prv-attachment").html(res.attachment);                            $("#prv-content").html(res.posts.post_content);                        } else {                            alert(res.msg);                        }                    }                });                $("#dialog-preview").dialog("open");            });            // 선택한 뉴스를 모두 삭제한다.            $("#btn-delete").click(function() {                var chkLength = $(".news_list:checked").length;                if (chkLength == 0) {                    alert("삭제할 뉴스를 선택해 주십시오.");                    return;                }                showLoader();                $.ajax({                    type : "POST",                    url : "./ajax/news-delete.php",                    data : $("#news-list-form").serialize(),                    dataType : "json",                    success : function(res) {                        if (res.code == "0") {                            location.href = "./news_list.php";                        } else {                            hideLoader();                            alert(res.msg);                        }                    }                });            });        });    </script><?phprequire_once ADMIN_PATH . '/admin-footer.php';?>